import React, {useState, useEffect} from 'react'

const DateTimeDisplay = () => {
    const[Time, setTime] = useState(new Date());

    useEffect(() => {
        setInterval(() => setTime(new Date()),1000)
    },[])
  return (
    <div>
    <p>{Time.toLocaleTimeString()}</p>
    </div>
  )
}

export default DateTimeDisplay
