  const [todos, setTodos] = useState([]);

  const addTodo = todo =>{
    setTodos([...todos, {id: uuidv4(), task:todo, completed:false, isEditing:false}]);
    console.log(todos)
  }
  return (
    <div className='Todo-wrapper'>
    <TodoForm addTodo = {addTodo}/>
    </div>
  )
}

export default TodoWrapper;
